function removerBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function escondeTodasAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}function toggleQuestions(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function ocultaElementosHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElemtosHeader(){document.querySelector("header").classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),o=document.querySelectorAll("[data-faq-question]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaElementosHeader:exibeElemtosHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){escondeTodasAbas(),removerBotaoAtivo(),e.target.classList.add("shows__tabs__button--is-active");e=e.target.dataset.tabButton;document.querySelector(`[data-tab-id=${e}]`).classList.add("shows__list--is-active")});for(let e=0;e<o.length;e++)o[e].addEventListener("click",toggleQuestions)});